// Variables globales para la serpiente, resoluci√≥n de la cuadr√≠cula, comida, y dimensiones de la cuadr√≠cula.
let snake;
let rez = 20; // Factor de resoluci√≥n para escalar todo el juego.
let food;
let w; // Ancho del campo de juego en "unidades" de juego, no en p√≠xeles.
let h; // Altura del campo de juego en "unidades" de juego, no en p√≠xeles.

// Funci√≥n de configuraci√≥n inicial para p5.js, se llama una vez al inicio.
function setup() {
  createCanvas(400, 400); // Crea un lienzo de 400x400 p√≠xeles.
  w = floor(width / rez); // Calcula el ancho del campo de juego en unidades de juego.
  h = floor(height / rez); // Calcula la altura del campo de juego en unidades de juego.
  frameRate(5); // Establece la velocidad del juego a 5 cuadros por segundo.
  snake = new Snake(); // Crea una nueva instancia de la serpiente.
  foodLocation(); // Coloca la comida en una ubicaci√≥n inicial aleatoria.
}

// Genera una nueva ubicaci√≥n para la comida en el campo de juego.
function foodLocation() {
  let x = floor(random(w)); // Posici√≥n aleatoria en el eje X.
  let y = floor(random(h)); // Posici√≥n aleatoria en el eje Y.
  food = createVector(x, y); // Crea un vector para la posici√≥n de la comida.
}

// Funci√≥n que se llama cada vez que se presiona una tecla.
function keyPressed() {
  // Cambia la direcci√≥n de la serpiente bas√°ndose en la tecla presionada.
  if (keyCode === LEFT_ARROW) {
    snake.setDir(-1, 0); // Mueve hacia la izquierda.
  } else if (keyCode === RIGHT_ARROW) {
    snake.setDir(1, 0); // Mueve hacia la derecha.
  } else if (keyCode === DOWN_ARROW) {
    snake.setDir(0, 1); // Mueve hacia abajo.
  } else if (keyCode === UP_ARROW) {
    snake.setDir(0, -1); // Mueve hacia arriba.
  } else if (key == ' ') {
    snake.grow(); // Hace crecer la serpiente al presionar la barra espaciadora.
  }
}

// Funci√≥n de dibujo que p5.js llama en bucle para animar el juego.
function draw() {
  scale(rez); // Escala todo el dibujo por el factor de resoluci√≥n.
  background(220); // Establece el color de fondo del lienzo.
  if (snake.eat(food)) {
    foodLocation(); // Si la serpiente come la comida, genera una nueva ubicaci√≥n para la comida.
  }
  snake.update(); // Actualiza la posici√≥n de la serpiente.
  snake.show(); // Dibuja la serpiente en el lienzo.

  // Comprueba si el juego ha terminado (la serpiente choca consigo misma o con el borde).
  if (snake.endGame()) {
    print("END GAME"); // Imprime un mensaje en la consola.
    background(255, 0, 0); // Cambia el color de fondo a rojo para indicar el fin del juego.
    noLoop(); // Detiene el bucle de dibujo, finalizando el juego.
  }

  // Dibuja la comida en el campo de juego.
  noStroke(); // No dibuja bordes para la comida.
  fill(255, 0, 0); // Establece el color de la comida a rojo.
  rect(food.x, food.y, 1, 1); // Dibuja la comida como un cuadrado.
}

class Snake {

  // Constructor: inicializa la serpiente con un segmento en el centro del campo de juego.
  constructor() {
    this.body = []; // Array para almacenar los segmentos del cuerpo de la serpiente.
    this.body[0] = createVector(floor(w/2), floor(h/2)); // Posici√≥n inicial en el centro.
    this.xdir = 0; // Direcci√≥n inicial en el eje X.
    this.ydir = 0; // Direcci√≥n inicial en el eje Y.
    this.len = 0; // Longitud inicial de la serpiente.
  }

  // setDir: Establece la direcci√≥n de movimiento de la serpiente.
  setDir(x, y) {
    this.xdir = x;
    this.ydir = y;
  }

  // update: Actualiza la posici√≥n de la serpiente moviendo el cuerpo.
  update() {
    let head = this.body[this.body.length-1].copy(); // Copia la posici√≥n actual de la cabeza.
    this.body.shift(); // Elimina el segmento m√°s antiguo del cuerpo.
    head.x += this.xdir; // Mueve la cabeza en el eje X.
    head.y += this.ydir; // Mueve la cabeza en el eje Y.
    this.body.push(head); // A√±ade la nueva posici√≥n de la cabeza al cuerpo.
  }

  // grow: Hace crecer la serpiente a√±adiendo un segmento al cuerpo.
  grow() {
    let head = this.body[this.body.length-1].copy(); // Copia la √∫ltima posici√≥n de la cabeza.
    this.len++; // Aumenta la longitud de la serpiente.
    this.body.push(head); // A√±ade el nuevo segmento al cuerpo.
  }

  // endGame: Comprueba si la serpiente ha chocado consigo misma o con el borde del campo de juego.
  endGame() {
    let x = this.body[this.body.length-1].x; // Posici√≥n X de la cabeza.
    let y = this.body[this.body.length-1].y; // Posici√≥n Y de la cabeza.
    // Comprueba colisi√≥n con bordes.
    if(x &gt; w-1 || x &lt; 0 || y &gt; h-1 || y &lt; 0) {
       return true;
    }
    // Comprueba colisi√≥n consigo misma.
    for(let i = 0; i &lt; this.body.length-1; i++) {
      let part = this.body[i];
      if(part.x == x && part.y == y) {
          return true;
      }
    }
    return false;
  }

  // eat: Determina si la serpiente ha comido una fruta.
  eat(pos) {
    let x = this.body[this.body.length-1].x; // Posici√≥n X de la cabeza.
    let y = this.body[this.body.length-1].y; // Posici√≥n Y de la cabeza.
    // Comprueba si la posici√≥n de la cabeza coincide con la fruta.
    if(x == pos.x && y == pos.y) {
      this.grow(); // Hace crecer la serpiente.
      return true;
    }
    return false;
  }

  // show: Dibuja la serpiente en el campo de juego.
  show() {
      for(let i = 0; i &lt; this.body.length; i++) {
        fill(0); // Color de relleno para la serpiente.
        noStroke(); // Sin borde para los segmentos.
        rect(this.body[i].x, this.body[i].y, 1, 1); // Dibuja cada segmento del cuerpo.
    }
  }
}

